import React from "react";
import { makeStyles, Typography, Paper } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  root: {
    position: "relative",
    borderRadius: 0,
    boxShadow: "none",
    cursor: "pointer",
    padding: theme.spacing(1),
  },
  selected: {
    backgroundColor: theme.palette.primary.main,
  },
}));

export const ContactListItem = ({ contact, setSelected, selected }) => {
  const classes = useStyles();

  let activeClasses;
  if (selected === contact.id) {
    activeClasses = { root: classes.root, selected: classes.selected };
  } else {
    activeClasses = { root: classes.root };
  }

  return (
    <Paper classes={activeClasses} onClick={() => setSelected(contact.id)}>
      <Typography
        key={contact.id}
        variant="subtitle2"
      >{`${contact.firstName} ${contact.lastName}`}</Typography>
    </Paper>
  );
};
