import React from "react";

import {
  makeStyles,
  Select,
  MenuItem,
  InputLabel,
  OutlinedInput,
} from "@material-ui/core";

import SearchIcon from "@material-ui/icons/Search";

import { FlexWrapper, InputWrapper } from "../styled/containers";

const useStyles = makeStyles({
  select: {
    minWidth: "212px",
  },
  label: {
    paddingLeft: "12px",
  },
  search: {},
});

const ContactsToolbarContext = React.createContext();

export const ContactsListToolbar = () => {
  const classes = useStyles();

  return (
    <FlexWrapper justifyContent="space-between">
      <InputWrapper width="none">
        <InputLabel className={classes.label} id="contact-filter-label">
          Sorting
        </InputLabel>
        <Select
          labelId="contact-filter-label"
          id="contact-filter-select"
          className={classes.select}
          defaultValue={sortValue}
          color="primary"
          variant="outlined"
          onChange={(e) => setSortValue(e.target.value)}
        >
          <MenuItem value="first-name">First Name</MenuItem>
          <MenuItem value="last-name">Last Name</MenuItem>
        </Select>
      </InputWrapper>

      <InputWrapper width="auto">
        <InputLabel className={classes.label}>Search</InputLabel>
        <OutlinedInput
          placeholder="First, Last, Company, Title"
          value={searchValue}
          label="Search"
          color="primary"
          notched={false}
          endAdornment={<SearchIcon />}
          variant="outlined"
          onChange={(e) => setSearchValue(e.target.value)}
        />
      </InputWrapper>
    </FlexWrapper>
  );
};
