import React, { useContext } from "react";

import {
  makeStyles,
  Grid,
  Table,
  TableHead,
  TableBody,
  TableRow,
  TableCell,
} from "@material-ui/core";

import { useRentalFilter, useRentalSearch } from "../../utils";
import { DriveToolbarContext } from "../../pages/DrivesListPage";

const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: theme.spacing(4),
    "& tr:hover": {
      backgroundColor: "red",
      boxShadow: theme.shadows[4],
      cursor: pointer,
    },
  },
}));

export const DriveList = ({ drives }) => {
  const { searchContext, filterContext } = useContext(DriveToolbarContext);

  let driveList = [...drives];
  //   let rentalList = useRentalFilter(
  //     rentals,
  //     filterContext.statusFilter,
  //     filterContext.configFilter,
  //     filterContext.typeFilter
  //   );

  //   rentalList = useRentalSearch(
  //     rentalList,
  //     searchContext.searchValue.toLowerCase()
  //   );

  const classes = useStyles();

  return (
    <Table className={classes.root}>
      <TableHead>
        <TableRow>
          <TableCell>Drive Number</TableCell>
          <TableCell>Capacity</TableCell>
          <TableCell>Available</TableCell>
          <TableCell>Current Project</TableCell>
        </TableRow>
      </TableHead>
      <TableBody>
        {driveList.map((drive) => (
          <TableRow key={drive.id} hover>
            <TableCell>{drive.driveNumber}</TableCell>
            <TableCell>{drive.driveCapacityGb}</TableCell>
            <TableCell>{drive.currentProject ? "No" : "Yes"}</TableCell>
            <TableCell>
              {drive.currentProject ? drive.currentProject.title : "---"}
            </TableCell>
          </TableRow>
        ))}
      </TableBody>
    </Table>
  );
};
