import React, { useState } from "react";

import { RentalContact } from "../RentalContact";
import { SectionHeading } from "../../../styled/typography";
import { SimpleDiv, PositionWrapper } from "../../../styled/containers";

import {
  makeStyles,
  Button,
  Select,
  MenuItem,
  Grid,
  FormControl,
} from "@material-ui/core";

import { NewContactModal } from "../../Contacts";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "80%",
    marginLeft: theme.spacing(6),
    paddingTop: theme.spacing(2),
  },
  testBorder: {
    border: "1px solid black",
  },
}));

export const RentalClients = ({ clients }) => {
  const [selectedClient, setSelectedClient] = useState("");
  const [selectedClientRole, setSelectedClientRole] = useState("");

  const classes = useStyles();

  return (
    <>
      <SectionHeading gridColumn>Clients</SectionHeading>
      <div className={classes.root}>
        <Grid container spacing={2}>
          {/* Grid Left */}
          <Grid item container xs={12} md={4}>
            {clients.map((client, index) => (
              <Grid key={client.id} item xs={12}>
                <RentalContact
                  client={client}
                  contactId={client.client.id}
                  firstName={client.client.firstName}
                  lastName={client.client.lastName}
                  index={index}
                />
              </Grid>
            ))}
          </Grid>
          {/* Grid Right */}
          <Grid
            item
            container
            xs={12}
            md={8}
            spacing={1}
            className={classes.testBorder}
          >
            {/* Form Start */}
            <Grid component="form" container item xs={12} spacing={1}>
              <Grid item xs={6} fullWidth>
                <FormControl fullWidth>
                  <Select
                    id="client-select-input"
                    labelId="client-role-select-label"
                    variant="outlined"
                    color="primary"
                    displayEmpty
                    fullWidth
                    value={selectedClient}
                    onChange={(e) => setSelectedClient(e.target.value)}
                  >
                    <MenuItem value="" disabled>
                      Client
                    </MenuItem>
                    <MenuItem value="Option A">George HartIsAGuy</MenuItem>
                  </Select>
                </FormControl>
              </Grid>

              <Grid item xs={6}>
                <FormControl fullWidth>
                  <Select
                    id="client-role-select-input"
                    labelId="client-role-select-label"
                    variant="outlined"
                    color="primary"
                    placeholder="Client Role"
                    fullWidth
                    value={selectedClientRole}
                    displayEmpty
                    onChange={(e) => setSelectedClientRole(e.target.value)}
                  >
                    <MenuItem value="" disabled>
                      Client Role
                    </MenuItem>
                    <MenuItem value="DX Mixer">DX Mixer</MenuItem>
                    <MenuItem value="MX Mixer">MX Mixer</MenuItem>
                    <MenuItem value="DX/MX Mixer">DX/MX Mixer</MenuItem>
                    <MenuItem value="FX Mixer">FX Mixer</MenuItem>
                    <MenuItem value="Recordist">Recordist</MenuItem>
                    <MenuItem value="Other">Other</MenuItem>
                  </Select>
                </FormControl>
              </Grid>
            </Grid>
            <Button size="small" variant="outlined" fullWidth>
              Add / Edit Client
            </Button>
            <Button size="small" variant="outlined" fullWidth>
              Remove Client
            </Button>
            {/* Form End */}
          </Grid>
        </Grid>
      </div>
      {/* <GridWrapper padding="19px 30px" justifyItems="start" columns="1fr 1.5fr">
        <GridWrapper
          columns="1fr 1fr"
          margin="13px 8px 0 0"
          alignSelf="start"
          justifyItems="start"
          alignItems="baseline"
        >
          {clients.map((client, index) => (
            <RentalContact
              client={client}
              contactId={client.client.id}
              firstName={client.client.firstName}
              lastName={client.client.lastName}
              index={index}
            />
          ))}
        </GridWrapper> */}

      <PositionWrapper position="absolute" bottom="5%" right="10%">
        <NewContactModal blackButton />
      </PositionWrapper>
      {/* </GridWrapper> */}
    </>
  );
};
