import React, { useState } from "react";
import { InputWrapper } from "../../styled/containers";
import { Label } from "../../styled/forms";

import {
  Button,
  Grid,
  InputLabel,
  OutlinedInput,
  Select,
  MenuItem,
} from "@material-ui/core";

import { useMutation } from "@apollo/react-hooks";
import { CREATE_DRIVE, ALL_DRIVES_QUERY, HOME_PAGE_QUERY } from "../../gql";

export const NewDriveForm = ({ toggle, nextDriveNumber }) => {
  const [createDrive, { error: createError }] = useMutation(CREATE_DRIVE);

  const [driveNumber, setDriveNumber] = useState(`${nextDriveNumber}`);
  const [driveCapacityGb, setDriveCapacityGb] = useState("2TB");
  const [numberOfDrives, setNumberOfDrives] = useState("1");

  const handleSubmit = (e) => {
    e.preventDefault();
    createDrive({
      variables: {
        driveNumber,
        driveCapacityGb,
        numberOfDrives,
      },
      refetchQueries: [{ query: ALL_DRIVES_QUERY }, { query: HOME_PAGE_QUERY }],
      onCompleted: createDriveCompleted(),
    });
  };

  const createDriveCompleted = () => {
    toggle();
  };

  return (
    <>
      <Grid container as="form" gap={2} onSubmit={(e) => handleSubmit(e)}>
        <Grid item xs={4}>
          <InputLabel>Number</InputLabel>
          <OutlinedInput
            placeholder="1"
            value={driveNumber}
            onChange={(e) => setDriveNumber(e.target.value)}
          />
        </Grid>

        <Grid item xs={4}>
          <InputLabel>Capacity</InputLabel>
          <Select
            color="primary"
            variant="outlined"
            fullWidth
            defaultValue={driveCapacityGb}
            onChange={(e) => setDriveCapacityGb(e.target.value)}
          >
            <MenuItem value="250GB">250GB</MenuItem>
            <MenuItem value="500GB">500GB</MenuItem>
            <MenuItem value="1TB">1TB</MenuItem>
            <MenuItem value="2TB">2TB</MenuItem>
          </Select>
        </Grid>

        <Grid item xs={4}>
          <InputLabel>Quantity</InputLabel>
          <OutlinedInput
            placeholder="1"
            value={numberOfDrives}
            onChange={(e) => setNumberOfDrives(e.target.value)}
          />
        </Grid>

        <Grid item xs={12}>
          <Button
            type="submit"
            size="large"
            fullWidth
            variant="contained"
            color="primary"
            disabled={
              !driveNumber.trim() || !driveCapacityGb | !numberOfDrives.trim()
            }
          >
            Create Drives
          </Button>
        </Grid>
      </Grid>
    </>
  );
};
