import React from "react";
import { Link } from "react-router-dom";

import { makeStyles, TableRow, TableCell, IconButton } from "@material-ui/core";
import EditIcon from "@material-ui/icons/Edit";
import DeleteForeverIcon from "@material-ui/icons/DeleteForever";
import ClearIcon from "@material-ui/icons/Clear";

const useStyles = makeStyles((theme) => ({
  root: {},
  actions: {
    minWidth: "25px",
    maxWidth: "25px",
    border: "1px solid black",
  },
}));

export const DriveListItem = ({ drive }) => {
  const classes = useStyles();
  return (
    <TableRow key={drive.id} hover>
      <TableCell>{drive.driveNumber}</TableCell>
      <TableCell>{drive.driveCapacityGb}</TableCell>
      <TableCell className={classes.showOnHover}>
        {drive.rentalProjects.length > 0 ? (
          <Link to={`/rentals/${drive.rentalProjects[0].project.id}`}>
            {drive.rentalProjects[0].project.title}
          </Link>
        ) : (
          "---"
        )}
      </TableCell>
      <TableCell className={classes.actions}>
        {drive.rentalProjects.length > 0 && (
          <IconButton>
            <ClearIcon />
          </IconButton>
        )}
        <IconButton>
          <EditIcon />
        </IconButton>
        <IconButton>
          <DeleteForeverIcon />
        </IconButton>
      </TableCell>
    </TableRow>
  );
};
