import React, { useState } from "react";
import { InputWrapper } from "../../styled/containers";
import { Label } from "../../styled/forms";

import {
  Button,
  Grid,
  InputLabel,
  OutlinedInput,
  Select,
  MenuItem,
  TextField,
  FormControl,
  Input,
} from "@material-ui/core";

import { useMutation } from "@apollo/react-hooks";
import { CREATE_DRIVE, ALL_DRIVES_QUERY, HOME_PAGE_QUERY } from "../../gql";

export const NewDriveForm = ({ toggle, nextDriveNumber }) => {
  const [createDrive, { error: createError }] = useMutation(CREATE_DRIVE);

  const [driveNumber, setDriveNumber] = useState(`${nextDriveNumber}`);
  const [driveCapacityGb, setDriveCapacityGb] = useState("2TB");
  const [driveQuantity, setDriveQuantity] = useState("1");

  const handleSubmit = (e) => {
    e.preventDefault();
    createDrive({
      variables: {
        driveNumber,
        driveCapacityGb,
        driveQuantity,
      },
      refetchQueries: [{ query: ALL_DRIVES_QUERY }, { query: HOME_PAGE_QUERY }],
      onCompleted: createDriveCompleted(),
    });
  };

  const createDriveCompleted = () => {
    toggle();
  };

  return (
    <>
      <Grid container as="form" gap={2} onSubmit={(e) => handleSubmit(e)}>
        <Grid item xs={4}>
          <TextField
            label="Drive Number"
            placeholder="ex. 1"
            variant="outlined"
            defaultValue={driveNumber}
            onChange={(e) => setDriveNumber(e.target.value)}
            InputLabelProps={{
              shrink: true,
            }}
          />
        </Grid>

        <Grid item xs={4}>
          <FormControl fullWidth variant="outlined">
            <InputLabel id="drive-capacity-label">Capacity</InputLabel>
            <Select
              labelId="drive-capacity-label"
              label="Capacity"
              id="drive-capacity-select"
              color="primary"
              fullWidth
              defaultValue={driveCapacityGb}
              onChange={(e) => setDriveCapacityGb(e.target.value)}
            >
              <MenuItem value="250GB">250GB</MenuItem>
              <MenuItem value="500GB">500GB</MenuItem>
              <MenuItem value="1TB">1TB</MenuItem>
              <MenuItem value="2TB">2TB</MenuItem>
            </Select>
          </FormControl>
        </Grid>
        {/* <Grid item xs={4}>
          <FormControl variant="outlined">
            <InputLabel id="demo-simple-select-outlined-label">Age</InputLabel>
            <Select
              labelId="demo-simple-select-outlined-label"
              id="demo-simple-select-outlined"
              value={10}
              label="Age"
            >
              <MenuItem value="">
                <em>None</em>
              </MenuItem>
              <MenuItem value={10}>Ten</MenuItem>
              <MenuItem value={20}>Twenty</MenuItem>
              <MenuItem value={30}>Thirty</MenuItem>
            </Select>
          </FormControl>
        </Grid> */}

        <Grid item xs={4}>
          <TextField
            label="Drive Quantity"
            placeholder="ex. 1"
            variant="outlined"
            defaultValue={driveQuantity}
            onChange={(e) => setDriveQuantity(e.target.value)}
            InputLabelProps={{
              shrink: true,
            }}
          />
        </Grid>

        <Grid item xs={12}>
          <Button
            type="submit"
            size="large"
            fullWidth
            variant="contained"
            color="primary"
            disabled={
              !driveNumber.trim() || !driveCapacityGb | !driveQuantity.trim()
            }
          >
            Create Drives
          </Button>
        </Grid>
      </Grid>
    </>
  );
};
