import React from "react";

import { useQuery } from "@apollo/react-hooks";
import { CONTACT_QUERY } from "../../gql";

import { makeStyles, Typography } from "@material-ui/core";

import { Error, Loading } from "../global";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(1),
  },
  heading: {},
  grid: {
    display: "grid",
    gridTemplateColumns: "40% 60%",
    gridGap: theme.spacing(2),
  },
}));

export const ContactDetail = ({ contactId }) => {
  const { data, loading, error } = useQuery(CONTACT_QUERY, {
    variables: { id: contactId },
  });

  const classes = useStyles();
  return (
    <>
      {contactId ? (
        <>
          {loading && <Loading />}
          {error && <Error error={error} />}
          {data && (
            <div className={classes.root}>
              <Typography
                className={classes.heading}
                align="center"
                variant="h4"
              >
                {`${data.contact.firstName} ${data.contact.lastName}`}
              </Typography>
              <div className={classes.grid}>
                <Typography align="right">Company</Typography>
                <Typography align="left">{data.contact.company}</Typography>
              </div>
              <div className={classes.grid}>
                <Typography align="right">Title</Typography>
                <Typography align="left">{data.contact.title}</Typography>
              </div>
              <div className={classes.grid}>
                <Typography align="right">Phone</Typography>
                <Typography align="left">{data.contact.phoneNumber}</Typography>
              </div>
              <div className={classes.grid}>
                <Typography align="right">Email</Typography>
                <Typography align="left">{data.contact.email}</Typography>
              </div>
              <div className={classes.grid}>
                <Typography align="right">Email</Typography>
                <div>
                  <Typography align="left">{data.contact.email}</Typography>
                </div>
              </div>

              {/* <div className={classes.firstColumn}>
                <Typography align="right">Company</Typography>
                <Typography align="right">Title</Typography>
                <Typography align="right">Phone</Typography>
                <Typography align="right">Email</Typography>
                <Typography align="right">Projects</Typography>
                <Typography align="right">Notes</Typography>
              </div>
              <div className={classes.secondColumn}>
                <Typography align="left">{data.contact.company}</Typography>
                <Typography align="left">{data.contact.title}</Typography>
                <Typography align="left">{data.contact.phoneNumber}</Typography>
                <Typography align="left">{data.contact.email}</Typography>
                <div>
                  <Typography align="left">Projects</Typography>
                  <Typography align="left">Projects</Typography>
                </div>
                <Typography align="left">{data.contact.notes}</Typography>
              </div> */}
            </div>
          )}
        </>
      ) : (
        <Typography className={classes.heading} align="center" variant="h4">
          Select a contact
        </Typography>
      )}
    </>
  );
};
