import React from "react";

import { useMutation } from "@apollo/react-hooks";
import { UPDATE_CONTACT } from "../../gql";

import {
  makeStyles,
  Typography,
  Button,
  TextField,
  InputLabel,
  FormControl,
  Select,
} from "@material-ui/core";

import { Error, Loading } from "../global";

const useStyles = makeStyles((theme) => ({
  root: {
    paddingTop: theme.spacing(1),
    paddingBottom: theme.spacing(1),
    paddingLeft: theme.spacing(3),
    paddingRight: theme.spacing(3),
  },
  heading: {},
  detailBody: {
    marginTop: theme.spacing(2),
    width: "100%",
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "20% 80%",
    gridGap: theme.spacing(2),
    marginBottom: theme.spacing(1),
  },
  label: {
    color: theme.palette.grey[700],
    fontSize: ".85em",
    alignSelf: "baseline",
    justifySelf: "stretch",
  },
}));

export const ContactEdit = ({ contactId }) => {
  const { data, loading, error } = useQuery(CONTACT_QUERY, {
    variables: { id: contactId },
  });

  const classes = useStyles();
  return (
    <>
      {loading && <Loading />}
      {error && <Error error={error} />}

      <form className={classes.root}>
        <Typography
          className={classes.heading}
          align="left"
          variant="h4"
          color="primary"
        >
          {`${data.contact.firstName} ${data.contact.lastName}`}
        </Typography>
        <div className={classes.detailBody}>
          <div className={classes.grid}>
            <Typography className={classes.label} align="right">
              Company
            </Typography>
            <Typography align="left">{data.contact.company}</Typography>
          </div>
          <div className={classes.grid}>
            <Typography className={classes.label} align="right">
              Title
            </Typography>
            <Typography align="left">{data.contact.title}</Typography>
          </div>
          <div className={classes.grid}>
            <Typography className={classes.label} align="right">
              Phone
            </Typography>
            <Typography align="left">{data.contact.phoneNumber}</Typography>
          </div>
          <div className={classes.grid}>
            <Typography className={classes.label} align="right">
              Email
            </Typography>
            <Typography align="left">{data.contact.email}</Typography>
          </div>

          <div className={classes.grid}>
            <Typography className={classes.label} align="right">
              Notes
            </Typography>
            <div>
              <Typography align="left">{data.contact.notes}</Typography>
            </div>
          </div>
        </div>
      </form>
    </>
  );
};
