import React from "react";

import { SectionHeading } from "../../../styled/typography";
import { darkGrey } from "../../../styled/defaults";
import {
  SimpleDiv,
  GridWrapper,
  PositionWrapper,
} from "../../../styled/containers";
import { EditBasicInfo } from "./ButtonModals/EditBasicInfo";
import { makeStyles, Grid, Typography } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "90%",
    marginLeft: theme.spacing(3),
  },
  gutter: {
    display: "flex",
    justifyContent: "flex-end",
  },
}));

export const RentalBasicInfo = ({ project }) => {
  const classes = useStyles();
  return (
    <>
      <SectionHeading gridColumn>Basic Information</SectionHeading>
      <div className={classes.root}>
        <Grid container spacing={3}>
          {/* Grid Left */}
          <Grid item container xs={6} spacing={1}>
            <Grid item xs={12}>
              <Typography>{project.abbreviation}</Typography>
            </Grid>
            <Grid item xs={12}>
              {project.primaryRoom ? (
                <Typography>{project.primaryRoom.room.name}</Typography>
              ) : (
                <Typography>No primary room set</Typography>
              )}
            </Grid>
            <Grid item xs={12}>
              <Typography>{project.channelConfig}</Typography>
            </Grid>
          </Grid>
          {/* Grid Right */}
          <Grid item container xs={6} spacing={1}>
            <Grid item xs={12}>
              <Typography>
                {project.driveUser ? project.driveUser : "Username not set"}
              </Typography>
            </Grid>
            <Grid item xs={12}>
              <Typography>
                {project.drivePass ? project.drivePass : "Password not set"}
              </Typography>
            </Grid>
            <Grid item xs={12}>
              <Typography>
                {project.msUser ? project.msUser : "Username not set"}
              </Typography>
            </Grid>
            <Grid item xs={12}>
              <Typography>
                {project.msPass ? project.msPass : "Pass not set"}
              </Typography>
            </Grid>
          </Grid>
          {/* Action Buttons */}
          <Grid item xs={12} className={classes.gutter}>
            <EditBasicInfo project={project} projectId={project.id} />
          </Grid>
        </Grid>
      </div>
    </>
  );
};
