import React, { useState, useContext } from "react";

import { makeStyles, Paper, Button, Typography } from "@material-ui/core";

import { useSortContactAlpha, useContactSearch, useToggle } from "../../utils";
import { ContactsToolbarContext } from "../../pages/ContactsListPage";
import { ContactListItem } from "./ContactListItem";
import { ContactDetail } from "./ContactDetail";
import { ContactListFilter, ContactListSearch } from "./ContactUtilities";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    justifyContent: "center",
    marginTop: theme.spacing(4),
    marginLeft: "auto",
    marginRight: "auto",
    maxWidth: "80%",
    borderRadius: theme.shape.borderRadius,
    height: "500px",
    boxShadow: theme.shadows[2],
    "& > div": {
      borderRadius: 0,
    },
  },
  contactList: {
    minWidth: "300px",
    boxShadow: "none",
    borderRightStyle: "solid",
    borderColor: theme.palette.grey[400],
    borderWidth: "1px",
    paddingTop: theme.spacing(2),
    paddingBottom: theme.spacing(2),
    overflowY: "auto",
  },
  clearButton: {
    display: "block",
    marginTop: theme.spacing(1),
    marginLeft: "auto",
    marginRight: "auto",
  },
  contactListItems: {
    marginTop: theme.spacing(1),
    "& :hover div": {
      backgroundColor: theme.palette.grey[400],
    },
  },
  contactInfo: {
    flexGrow: 1,
    minHeight: "300px",
    overflowY: "auto",
    padding: theme.spacing(2),
  },
}));

export const ContactList = ({ contacts }) => {
  const [selected, setSelected] = useState(null);
  const { isShowing, toggle } = useToggle();
  const { searchContext, sortContext } = useContext(ContactsToolbarContext);

  let contactsList = useContactSearch(
    contacts,
    searchContext.searchValue.toLowerCase()
  );
  contactsList = useSortContactAlpha(contactsList, sortContext.sortValue);

  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Paper className={classes.contactList}>
        <ContactListSearch />
        <ContactListFilter />
        {searchContext.searchValue && (
          <Button
            className={classes.clearButton}
            color="primary"
            onClick={() => searchContext.setSearchValue("")}
          >
            Clear Search
          </Button>
        )}
        <div className={classes.contactListItems}>
          {contactsList.length > 0 ? (
            contactsList.map((contact) => (
              <ContactListItem
                key={contact.id}
                contact={contact}
                setSelected={setSelected}
                isShowing={isShowing}
                toggle={toggle}
              />
            ))
          ) : (
            <Typography align="center" variant="h5">
              No Items To Display
            </Typography>
          )}
        </div>
      </Paper>
      <Paper className={classes.contactInfo} elevation={0}>
        <ContactDetail
          contactId={selected}
          setContactId={setSelected}
          isShowing={isShowing}
          toggle={toggle}
        />
      </Paper>
    </div>
  );
};
