export const useDrivesFilter = (drivesList, filterStatus) => {
  function getFilterStatus(drivesList, filterStatus) {
    switch (filterStaus) {
      case "1":
        return drivesList.filter((drive) => drive.rentalProjects.length === 0);
      case "2":
        return drivesList.filter((drive) => drive.rentalProjects.length === 1);
      case "0":
        return drivesList;
      default:
        return drivesList;
    }
  }

  const filteredList = getFilterStatus(drivesList, filterStatus);
  return filteredList;
};
