export const useRentalSearch = (rentalList, searchValue) => {
  const returnList = [];
  if (searchValue === "") {
    return rentalList;
  } else {
    rentalList.map(
      (rental) =>
        rental.title.indexOf(searchValue) > -1 ||
        (rental.abbreviation.indexOf(searchValue) > -1 &&
          returnList.push(rental))
    );
  }
  return returnList;
};
